// Pagination configuration
const ARTICLES_PER_PAGE = 6;

// Function to highlight search terms in text
function highlightSearchTerm(text, searchTerm) {
  if (!searchTerm || searchTerm.trim() === "") {
    return text;
  }

  const searchTerms = searchTerm.toLowerCase().trim().split(/\s+/);
  let result = text;

  // Create a regex that matches any of the search terms, case insensitive
  const regex = new RegExp(
    "(" +
      searchTerms
        .map((term) => term.replace(/[.*+?^${}()|[\]\\]/g, "\\$&"))
        .join("|") +
      ")",
    "gi"
  );

  // Replace matches with highlighted span
  result = result.replace(regex, '<span class="search-highlight">$1</span>');

  return result;
}

// Function to generate article cards for homepage with pagination
function generateArticleCards(
  page = 1,
  filteredArticles = null,
  searchTerm = null
) {
  const articlesGrid = document.querySelector(".articles-grid");
  if (!articlesGrid) return;

  articlesGrid.innerHTML = "";

  // Use filtered articles if provided, otherwise use all articles
  let articlesToShow = filteredArticles || articles;

  // Apply sorting
  const sortSelect = document.querySelector("#sort-select");
  if (sortSelect) {
    const sortValue = sortSelect.value;

    if (sortValue === "newest") {
      articlesToShow = [...articlesToShow].sort((a, b) => {
        const dateA = a.dateValue || new Date(a.date);
        const dateB = b.dateValue || new Date(b.date);
        return dateB - dateA;
      });
    } else if (sortValue === "popular") {
      articlesToShow = [...articlesToShow].sort((a, b) => {
        return b.rating - a.rating;
      });
    }
  }

  // Calculate pagination
  const startIndex = (page - 1) * ARTICLES_PER_PAGE;
  const endIndex = startIndex + ARTICLES_PER_PAGE;
  const paginatedArticles = articlesToShow.slice(startIndex, endIndex);

  // If no articles to show, display message
  if (paginatedArticles.length === 0) {
    articlesGrid.innerHTML =
      '<p class="no-results">No articles found in this category.</p>';
    return;
  }

  // Generate article cards
  paginatedArticles.forEach((article) => {
    // Prepare article data, highlighting search terms if needed
    let title = article.title;
    let excerpt = article.excerpt;

    if (searchTerm) {
      title = highlightSearchTerm(title, searchTerm);
      excerpt = highlightSearchTerm(excerpt, searchTerm);
    }

    const articleCard = document.createElement("div");
    articleCard.className = "article-card";
    articleCard.innerHTML = `
            <div class="article-card-image" style="background-image: url('${
              article.image
            }')">
              <a href="article.html?id=${
                article.id
              }" class="article-image-link"></a>
            </div>
            <div class="article-card-content">
                <div class="article-card-meta">
                    <span class="category">${article.category}</span>
                    <span class="date">${article.date}</span>
                </div>
                <h2><a href="article.html?id=${article.id}">${title}</a></h2>
                <p>${excerpt}</p>
                <div class="article-rating">
                    <span class="rating-value">${article.rating.toFixed(
                      1
                    )}</span>
                    <span class="rating-stars">${generateStars(
                      article.rating
                    )}</span>
                </div>
                <a href="article.html?id=${
                  article.id
                }" class="read-more">Read more</a>
            </div>
        `;
    articlesGrid.appendChild(articleCard);
  });

  // Update pagination UI
  updatePagination(
    page,
    articlesToShow.length,
    filteredArticles !== null,
    searchTerm
  );
}

// Function to generate star ratings
function generateStars(rating) {
  const fullStars = Math.floor(rating);
  const halfStar = rating % 1 >= 0.5;
  const emptyStars = 5 - fullStars - (halfStar ? 1 : 0);

  let starsHTML = "";

  // Add full stars
  for (let i = 0; i < fullStars; i++) {
    starsHTML += '<i class="fas fa-star"></i>';
  }

  // Add half star if needed
  if (halfStar) {
    starsHTML += '<i class="fas fa-star-half-alt"></i>';
  }

  // Add empty stars
  for (let i = 0; i < emptyStars; i++) {
    starsHTML += '<i class="far fa-star"></i>';
  }

  return starsHTML;
}

// Function to update pagination buttons
function updatePagination(
  currentPage,
  totalArticles,
  isFiltered,
  searchTerm = null
) {
  const paginationContainer = document.querySelector(".pagination");
  if (!paginationContainer) return;

  const totalPages = Math.ceil(totalArticles / ARTICLES_PER_PAGE);
  paginationContainer.innerHTML = "";

  // Only show pagination if there's more than one page
  if (totalPages <= 1) {
    paginationContainer.style.display = "none";
    return;
  }

  paginationContainer.style.display = "flex";

  // Add previous button if not on first page
  if (currentPage > 1) {
    const prevButton = document.createElement("button");
    prevButton.innerHTML = '<i class="fas fa-chevron-left"></i>';
    prevButton.addEventListener("click", () =>
      handlePageChange(currentPage - 1, isFiltered, searchTerm)
    );
    paginationContainer.appendChild(prevButton);
  }

  // Determine which page buttons to show
  let startPage = Math.max(1, currentPage - 2);
  let endPage = Math.min(totalPages, startPage + 4);

  // Adjust start page if we're near the end
  if (endPage - startPage < 4) {
    startPage = Math.max(1, endPage - 4);
  }

  // Add page buttons
  for (let i = startPage; i <= endPage; i++) {
    const pageButton = document.createElement("button");
    pageButton.textContent = i;
    if (i === currentPage) {
      pageButton.classList.add("active");
    }
    pageButton.addEventListener("click", () =>
      handlePageChange(i, isFiltered, searchTerm)
    );
    paginationContainer.appendChild(pageButton);
  }

  // Add next button if not on last page
  if (currentPage < totalPages) {
    const nextButton = document.createElement("button");
    nextButton.innerHTML = '<i class="fas fa-chevron-right"></i>';
    nextButton.addEventListener("click", () =>
      handlePageChange(currentPage + 1, isFiltered, searchTerm)
    );
    paginationContainer.appendChild(nextButton);
  }
}

// Function to handle page changes
function handlePageChange(page, isFiltered, searchTerm = null) {
  // If we have a search term, we need to handle search pagination
  if (searchTerm) {
    const searchResults = searchArticles(searchTerm);
    generateArticleCards(page, searchResults, searchTerm);
  }
  // If filtering by category, we need to get the current category
  else if (isFiltered) {
    const activeButton = document.querySelector(".categories button.active");
    if (activeButton) {
      const category = activeButton.textContent;
      if (category === "All") {
        generateArticleCards(page);
      } else {
        const filteredArticles = articles.filter(
          (article) => article.category === category
        );
        generateArticleCards(page, filteredArticles);
      }
    }
  } else {
    generateArticleCards(page);
  }

  // Scroll to top of articles section
  const articlesSection = document.querySelector(".articles-grid");
  if (articlesSection) {
    articlesSection.scrollIntoView({ behavior: "smooth" });
  }
}

// Function to filter articles by category
function filterArticles(category) {
  let filteredArticles = articles;

  if (category !== "All") {
    filteredArticles = articles.filter(
      (article) => article.category === category
    );
  }

  // Generate cards for first page of filtered articles
  generateArticleCards(1, category !== "All" ? filteredArticles : null);
}

// Function to load single article page
function loadArticlePage() {
  const articleContent = document.getElementById("article-content");
  if (!articleContent) return;

  const urlParams = new URLSearchParams(window.location.search);
  const articleId = parseInt(urlParams.get("id"));

  const article = articles.find((a) => a.id === articleId);

  if (article) {
    // Update page title
    document.title = `${article.title} - Tesla Crafted`;

    // Set article content
    document.getElementById("article-title").textContent = article.title;
    document.getElementById("article-category").textContent = article.category;
    document.getElementById("article-date").textContent = article.date;
    document.getElementById("article-read-time").textContent = article.readTime;
    document.getElementById("author-name").textContent = article.author;
    document.getElementById("author-image").src = article.authorImage;

    // Add hero image with optional link
    const articleImageContainer = document.getElementById(
      "article-image-container"
    );
    if (articleImageContainer) {
      if (article.url) {
        articleImageContainer.innerHTML = `
          <a href="${article.url}" target="_blank">
            <img id="article-image" src="${article.image}" alt="${article.title}">
          </a>
        `;
      } else {
        articleImageContainer.innerHTML = `
          <img id="article-image" src="${article.image}" alt="${article.title}">
        `;
      }
    } else {
      document.getElementById("article-image").src = article.image;
    }

    document.getElementById("article-content").innerHTML = article.content;

    // Load related articles (excluding current article)
    loadRelatedArticles(articleId, article.category);
  } else {
    // Handle article not found
    articleContent.innerHTML = "<p>Article not found.</p>";
  }
}

// Function to load related articles
function loadRelatedArticles(currentArticleId, category) {
  const relatedGrid = document.querySelector(".related-grid");
  if (!relatedGrid) return;

  // Find articles in the same category, excluding current article
  const relatedArticles = articles
    .filter(
      (article) =>
        article.id !== currentArticleId && article.category === category
    )
    .slice(0, 3); // Limit to 3 related articles

  // If we don't have enough related articles in the same category, add some from other categories
  if (relatedArticles.length < 3) {
    const additionalArticles = articles
      .filter(
        (article) =>
          article.id !== currentArticleId && !relatedArticles.includes(article)
      )
      .slice(0, 3 - relatedArticles.length);

    relatedArticles.push(...additionalArticles);
  }

  relatedGrid.innerHTML = "";

  relatedArticles.forEach((article) => {
    const articleCard = document.createElement("div");
    articleCard.className = "article-card";
    articleCard.innerHTML = `
            <div class="article-card-image" style="background-image: url('${article.image}')"></div>
            <div class="article-card-content">
                <div class="article-card-meta">
                    <span class="category">${article.category}</span>
                    <span class="date">${article.date}</span>
                </div>
                <h2><a href="article.html?id=${article.id}">${article.title}</a></h2>
                <a href="article.html?id=${article.id}" class="read-more">Read more</a>
            </div>
        `;
    relatedGrid.appendChild(articleCard);
  });
}

// Function to handle category filtering
function setupCategoryFilters() {
  const categoryButtons = document.querySelectorAll(".categories button");
  if (categoryButtons.length === 0) return;

  categoryButtons.forEach((button) => {
    button.addEventListener("click", function () {
      // Remove active class from all buttons
      categoryButtons.forEach((btn) => btn.classList.remove("active"));

      // Add active class to clicked button
      this.classList.add("active");

      const category = this.textContent;
      filterArticles(category);
    });
  });
}

// Function to search articles
function searchArticles(query) {
  if (!query || query.trim() === "") {
    return articles; // Return all articles if query is empty
  }

  query = query.toLowerCase().trim();

  return articles.filter((article) => {
    // Search in title
    const titleMatch = article.title.toLowerCase().includes(query);

    // Search in content (stripping HTML tags)
    const contentText = article.content.replace(/<[^>]*>/g, "");
    const contentMatch = contentText.toLowerCase().includes(query);

    // Search in excerpt
    const excerptMatch = article.excerpt.toLowerCase().includes(query);

    return titleMatch || contentMatch || excerptMatch;
  });
}

// Function to handle search form submission
function setupSearchHandler() {
  const searchForms = document.querySelectorAll(".search-bar");
  if (searchForms.length === 0) return;

  searchForms.forEach((form) => {
    const searchInput = form.querySelector("input");
    const searchButton = form.querySelector("button");

    // Handle search button click
    searchButton.addEventListener("click", function (e) {
      e.preventDefault();
      performSearch(searchInput.value);
    });

    // Handle pressing Enter in search input
    searchInput.addEventListener("keypress", function (e) {
      if (e.key === "Enter") {
        e.preventDefault();
        performSearch(searchInput.value);
      }
    });
  });
}

// Function to perform search and display results
function performSearch(query) {
  // Redirect to index page with search query if on another page
  if (!document.querySelector(".articles-grid")) {
    window.location.href = `index.html?search=${encodeURIComponent(query)}`;
    return;
  }

  // Clear active category
  const categoryButtons = document.querySelectorAll(".categories button");
  categoryButtons.forEach((btn) => btn.classList.remove("active"));

  // Find and activate "All" button
  const allButton = Array.from(categoryButtons).find(
    (btn) => btn.textContent === "All"
  );
  if (allButton) {
    allButton.classList.add("active");
  }

  // Perform search
  const searchResults = searchArticles(query);

  // Display search results as first page
  generateArticleCards(1, searchResults, query);

  // Update page heading or add a search results indicator
  const articlesSection = document.querySelector(".articles-grid");
  if (articlesSection) {
    // Add search indicator above articles grid
    let searchIndicator = document.querySelector(".search-indicator");
    if (!searchIndicator) {
      searchIndicator = document.createElement("div");
      searchIndicator.className = "search-indicator";
      articlesSection.parentNode.insertBefore(searchIndicator, articlesSection);
    }

    searchIndicator.innerHTML =
      searchResults.length > 0
        ? `<p>Found ${searchResults.length} results for: <strong>"${query}"</strong></p>
         <button class="clear-search">Clear search</button>`
        : `<p>No results found for: <strong>"${query}"</strong></p>
         <button class="clear-search">Clear search</button>`;

    // Add event listener to clear search button
    document
      .querySelector(".clear-search")
      .addEventListener("click", function () {
        // Remove search indicator
        searchIndicator.remove();

        // Clear search input
        document.querySelectorAll(".search-bar input").forEach((input) => {
          input.value = "";
        });

        // Reset to all articles
        generateArticleCards(1);
      });
  }
}

// Newsletter form validation
function validateEmail(email) {
  const regex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
  return regex.test(email);
}

function setupNewsletterValidation() {
  const newsletterForms = document.querySelectorAll(".newsletter-form");

  newsletterForms.forEach((form) => {
    // Create tooltip element if it doesn't exist
    if (!form.querySelector(".newsletter-tooltip")) {
      const tooltip = document.createElement("div");
      tooltip.className = "newsletter-tooltip";
      form.appendChild(tooltip);
    }

    const input = form.querySelector("input");
    const button = form.querySelector("button");
    const tooltip = form.querySelector(".newsletter-tooltip");

    button.addEventListener("click", function (e) {
      e.preventDefault();

      const email = input.value.trim();

      if (email === "") {
        tooltip.textContent = "Please enter your email address";
        tooltip.classList.add("show");
        setTimeout(() => tooltip.classList.remove("show"), 3000);
        return;
      }

      if (!validateEmail(email)) {
        tooltip.textContent = "Please enter a valid email address";
        tooltip.classList.add("show");
        setTimeout(() => tooltip.classList.remove("show"), 3000);
        return;
      }

      // Success
      tooltip.textContent = "Thank you for subscribing!";
      tooltip.classList.add("show");
      setTimeout(() => tooltip.classList.remove("show"), 3000);
      input.value = ""; // Clear the input field
    });
  });
}

// Setup sort handler
function setupSortHandler() {
  const sortSelect = document.querySelector("#sort-select");
  if (!sortSelect) return;

  sortSelect.addEventListener("change", function () {
    // Get currently active category
    const activeCategory = document.querySelector(".categories button.active");
    let filteredArticles = null;

    if (activeCategory && activeCategory.textContent !== "All") {
      filteredArticles = articles.filter(
        (article) => article.category === activeCategory.textContent
      );
    }

    // Regenerate articles with current filter but updated sort
    generateArticleCards(1, filteredArticles);
  });
}

// Handle sticky Tesla referral banner
function handleStickyBanner() {
  const banner = document.querySelector(".tesla-referral-banner");
  const footer = document.querySelector("footer");

  if (!banner || !footer) return;

  // Function to check if element is in viewport
  function isInViewport(element) {
    const rect = element.getBoundingClientRect();
    return (
      rect.top <=
        (window.innerHeight || document.documentElement.clientHeight) &&
      rect.bottom >= 0
    );
  }

  // Function to handle scroll events
  function handleScroll() {
    // Check if footer is in viewport
    if (isInViewport(footer)) {
      // Add footer-sticky class to banner
      banner.classList.add("footer-sticky");
      banner.classList.remove("sticky");
    } else {
      // Remove footer-sticky class from banner
      banner.classList.remove("footer-sticky");

      // Add sticky class if scrolled down
      if (window.scrollY > 100) {
        banner.classList.add("sticky");
      } else {
        banner.classList.remove("sticky");
      }
    }
  }

  // Add scroll event listener
  window.addEventListener("scroll", handleScroll);

  // Initial check
  handleScroll();
}

// Initialize the page
document.addEventListener("DOMContentLoaded", function () {
  // Check if we're on the homepage or article page
  if (document.querySelector(".articles-grid")) {
    // Check for URL parameters
    const urlParams = new URLSearchParams(window.location.search);
    const categoryParam = urlParams.get("category");
    const pageParam = parseInt(urlParams.get("page")) || 1;
    const searchParam = urlParams.get("search");

    setupCategoryFilters();
    setupSearchHandler();
    setupSortHandler();

    // If we have a search query, perform search
    if (searchParam) {
      // Fill search input with the query
      document.querySelectorAll(".search-bar input").forEach((input) => {
        input.value = searchParam;
      });

      // Perform search
      performSearch(searchParam);
    }
    // If category parameter exists, filter articles by that category
    else if (categoryParam) {
      // Find the category button and trigger a click
      const categoryButtons = document.querySelectorAll(".categories button");
      let categoryFound = false;

      categoryButtons.forEach((btn) => {
        if (btn.textContent === categoryParam) {
          btn.click();
          categoryFound = true;
        }
      });

      // Update active state in navigation
      const navLinks = document.querySelectorAll(".nav-links a");
      navLinks.forEach((link) => {
        link.classList.remove("active");
        if (link.getAttribute("href").includes(`category=${categoryParam}`)) {
          link.classList.add("active");
        }
      });

      // If category is valid, filter and paginate
      if (categoryFound) {
        const filteredArticles = articles.filter(
          (article) => article.category === categoryParam
        );
        generateArticleCards(pageParam, filteredArticles);
      } else {
        // If category not found, just show all articles
        generateArticleCards(pageParam);
      }
    } else {
      // No category filter, just show all articles with pagination
      generateArticleCards(pageParam);
    }
  } else if (document.querySelector(".article-container")) {
    loadArticlePage();
    setupSearchHandler();

    // Set active nav link based on article category
    const articleCategory =
      document.getElementById("article-category").textContent;
    const navLinks = document.querySelectorAll(".nav-links a");
    navLinks.forEach((link) => {
      link.classList.remove("active");
      if (link.getAttribute("href").includes(`category=${articleCategory}`)) {
        link.classList.add("active");
      }
    });
  }

  // Add animation to elements
  document.querySelectorAll(".article-card").forEach((card, index) => {
    card.style.animationDelay = `${index * 0.1}s`;
  });

  // Cookie Consent Functionality
  const cookieConsent = document.getElementById("cookieConsent");
  const acceptBtn = document.getElementById("cookieAccept");
  const declineBtn = document.getElementById("cookieDecline");

  // Check if user has already made a choice
  const cookieChoice = localStorage.getItem("cookieConsent");

  if (!cookieChoice && cookieConsent) {
    // Show the cookie consent popup if no choice has been made
    setTimeout(() => {
      cookieConsent.classList.add("show");
    }, 1000);
  }

  // Handle accept button click
  if (acceptBtn) {
    acceptBtn.addEventListener("click", function () {
      localStorage.setItem("cookieConsent", "accepted");
      cookieConsent.classList.remove("show");
    });
  }

  // Handle decline button click
  if (declineBtn) {
    declineBtn.addEventListener("click", function () {
      localStorage.setItem("cookieConsent", "declined");
      cookieConsent.classList.remove("show");
    });
  }

  // Setup newsletter form validation
  setupNewsletterValidation();

  // Initialize sticky banner
  handleStickyBanner();
});
